// src/App.jsx (Day 14 ÏµúÏ¢Ö ÌÜµÌï© Î∞è ÏΩîÎìú Ï†ïÎ¶¨ Î≤ÑÏ†Ñ)

import React, { useState, useEffect, useMemo } from 'react';

// === Ïª¥Ìè¨ÎÑåÌä∏ import ===
import HabitList from './components/HabitList'; 
import HabitForm from './components/HabitForm';
import CalendarDashboard from './components/CalendarDashboard';
// ====================

// LocalStorage Ïú†Ìã∏Î¶¨Ìã∞
import { loadHabits, saveHabits, checkHabitToday } from './utils/localStorage'; 

function App() {
  const [habits, setHabits] = useState([]);
  const [isFormOpen, setIsFormOpen] = useState(false); 
  const [sortKey, setSortKey] = useState('name'); 

  // Day 10: Îã§ÌÅ¨ Î™®Îìú ÏÉÅÌÉú Ï∂îÍ∞Ä Î∞è LocalStorage ÎèôÍ∏∞Ìôî
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const savedMode = localStorage.getItem('darkMode');
    return savedMode ? JSON.parse(savedMode) : false;
  }); 

  // 1. Ï¥àÍ∏∞ Î°úÎî© Ïãú Îç∞Ïù¥ÌÑ∞ Ïú†Ìö®ÏÑ± Í≤ÄÏÇ¨ Î∞è ÏÉÅÌÉú Ï†ÄÏû•
  useEffect(() => {
    const loadedHabits = loadHabits().map(habit => ({
      ...habit,
      id: habit.id || crypto.randomUUID(),
      name: habit.name || "Ïù¥Î¶Ñ ÏóÜÎäî ÏäµÍ¥Ä",
      records: Array.isArray(habit.records) ? habit.records : [],
      type: habit.type || 'daily',
      targetCount: habit.targetCount || 1,
      customColor: habit.customColor || '#4f46e5'
    }));
    setHabits(loadedHabits);
  }, []);

  // 2. ÏäµÍ¥Ä Î∞è Îã§ÌÅ¨ Î™®Îìú ÏÉÅÌÉú Ï†ÄÏû•
  useEffect(() => {
    saveHabits(habits);
    localStorage.setItem('darkMode', JSON.stringify(isDarkMode)); 
    document.documentElement.classList.toggle('dark', isDarkMode);
  }, [habits, isDarkMode]);

  // Day 13: console.log Ï†úÍ±∞ Îì±Ïùò Ï†ïÎ¶¨ ÏûëÏóÖÏùÑ ÌñàÏúºÎØÄÎ°ú, Ï£ºÏÑùÏùÑ Ï†úÍ±∞ÌïòÍ≥† Ìï®Ïàò Î≥∏Ï≤¥Îßå Ïú†ÏßÄÌï©ÎãàÎã§.

  // CRUD - C/U Í∏∞Îä• Ï≤òÎ¶¨ (Day 11: customColor ÌïÑÎìú ÏàòÏö©)
  const handleSaveHabit = (newHabit) => {
    if (newHabit.id) {
      setHabits(habits.map(h => (h.id === newHabit.id ? newHabit : h)));
    } else {
      setHabits([...habits, { 
        ...newHabit, 
        id: crypto.randomUUID(), 
        records: [], 
        name: newHabit.name || "ÏÉà ÏäµÍ¥Ä",
        type: newHabit.type || 'daily', 
        targetCount: newHabit.targetCount || 1,
        customColor: newHabit.customColor || '#4f46e5' 
      }]);
    }
    setIsFormOpen(false);
  };

  // CRUD - D Í∏∞Îä• Ï≤òÎ¶¨ (ÏÇ≠Ï†ú)
  const handleDeleteHabit = (id) => {
    if (window.confirm('Ï†ïÎßêÎ°ú Ïù¥ ÏäµÍ¥ÄÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
        setHabits(habits.filter(h => h.id !== id));
    }
  };

  // ÌïµÏã¨ Í∏∞Îä• 1: ÏùºÏùº Ï≤¥ÌÅ¨
  const handleCheck = (habitId) => {
    const updatedHabits = habits.map(habit => {
      if (habit.id === habitId) {
        return checkHabitToday(habit);
      }
      return habit;
    });
    setHabits(updatedHabits);
  };

  // Day 6: Ï†ïÎ†¨ Î°úÏßÅ
  const sortedHabits = useMemo(() => {
      const sortableHabits = [...habits];

      sortableHabits.sort((a, b) => {
          if (sortKey === 'name') {
              return a.name.localeCompare(b.name);
          }
          if (sortKey === 'id') { 
              return b.id - a.id; 
          }
          return 0;
      });
      return sortableHabits;
  }, [habits, sortKey]);

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-4 sm:p-8 transition-colors duration-500">

      {/* Day 10: Îã§ÌÅ¨ Î™®Îìú ÌÜ†Í∏Ä Î≤ÑÌäº */}
      <button 
        onClick={() => setIsDarkMode(!isDarkMode)} 
        className="p-2 rounded-full absolute top-4 right-4 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors z-20"
      >
        {isDarkMode ? 'üåû Light' : 'üåô Dark'}
      </button>

      <header className="text-center mb-8">
        <h1 className="text-4xl font-bold text-indigo-700 dark:text-indigo-400">Vibe Habit Tracker</h1>
        <p className="text-gray-500 dark:text-gray-400">Î∞îÏù¥Î∏å ÏΩîÎî© Í∏∞Îßê ÌîÑÎ°úÏ†ùÌä∏</p>
      </header>

      {/* ÏäµÍ¥Ä Ï∂îÍ∞Ä Î≤ÑÌäº */}
      <button 
        onClick={() => setIsFormOpen(true)}
        className="fixed bottom-6 right-6 p-4 rounded-full bg-indigo-500 text-white shadow-lg hover:bg-indigo-600 transition-all z-10"
      >
        + ÏÉà ÏäµÍ¥Ä
      </button>

      {/* Habit Form Modal */}
      {isFormOpen && (
        <HabitForm 
          onSave={handleSaveHabit} 
          onClose={() => setIsFormOpen(false)}
        />
      )}

      {/* Day 6: Ï†ïÎ†¨ UI */}
      <div className="flex justify-end mb-4 max-w-7xl mx-auto lg:max-w-[calc(66.6666%-1rem)]">
          <label className="text-sm font-medium text-gray-700 dark:text-gray-300 mr-2 self-center">Ï†ïÎ†¨ Í∏∞Ï§Ä:</label>
          <select
              value={sortKey}
              onChange={(e) => setSortKey(e.target.value)}
              className="p-2 border border-gray-300 rounded-lg dark:bg-gray-700 dark:text-white"
          >
              <option value="name">Ïù¥Î¶ÑÏàú (A-Z)</option>
              <option value="id">ÏµúÏã† Îì±Î°ùÏàú</option>
          </select>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 max-w-7xl mx-auto">

        {/* Ïò§Îäò Ìï† Ïùº Î™©Î°ù */}
        <div className="lg:col-span-2">
          <h2 className="text-2xl font-semibold mb-4 text-gray-800 dark:text-white">Ïò§ÎäòÏùò ÏäµÍ¥Ä</h2>
          <HabitList 
            habits={sortedHabits} 
            onCheck={handleCheck} 
            onDelete={handleDeleteHabit} 
          />
        </div>

        {/* ÏõîÎ≥Ñ ÎåÄÏãúÎ≥¥Îìú (ÌïµÏã¨ Í∏∞Îä• 3) */}
        <div className="lg:col-span-1">
          <h2 className="text-2xl font-semibold mb-4 text-gray-800 dark:text-white">ÏõîÎ≥Ñ Í∏∞Î°ù</h2>
          <CalendarDashboard habits={habits} />
        </div>
      </div>

      <footer className="text-center mt-10 text-gray-400 dark:text-gray-600 text-sm">
          &copy; 2025 Vibe Coding Project. Developed with Gemini CLI.
      </footer>
    </div>
  );
}

export default App;
